package com.common.library.calendar;

import java.text.SimpleDateFormat;
import java.util.Locale;

/**
 * Different kinds of date style can be generated by supply a datetime unit.<br>
 * <p>
 * {@code DateTimeTag unit = new DateTimeTag("-", "-", "", ":", ":", "")},
 * this code snip will generate date format as "yyyy-MM-dd HH:mm:sss".<br>
 * <p>
 * <pre>
 * Above code snip can also write like below:
 * DateTimeTag tag = new DateTimeTag();
 * tag.setYearSuffix("年");
 * tag.setMonthSuffix("月");
 * tag.setDaySuffix("日");
 * tag.setHourSuffix("点");
 * tag.setMinuteSuffix("分");
 * tag.setSecondSuffix("秒");
 * </pre>
 */
public class DateSuffix {
	private String yearSuffix;
	private String monthSuffix;
	private String daySuffix;
	private String hourSuffix;
	private String minuteSuffix;
	private String secondSuffix;

	/**
	 * The default date style is yyyy-dd-MM HH:mm:ss.
	 */
	public DateSuffix() {
		this.yearSuffix = "-";
		this.monthSuffix = "";
		this.daySuffix = "";
		this.hourSuffix = ":";
		this.minuteSuffix = ":";
		this.secondSuffix = "";
	}

	/**
	 * If some unit set as "null", they will not be included in generated format.
	 */
	public DateSuffix(String yearSuffix, String monthTag, String daySuffix,
					  String hourSuffix, String minuteSuffix, String secondSuffix) {
		this.yearSuffix = yearSuffix;
		this.monthSuffix = monthTag;
		this.daySuffix = daySuffix;
		this.hourSuffix = hourSuffix;
		this.minuteSuffix = minuteSuffix;
		this.secondSuffix = secondSuffix;
	}


	public SimpleDateFormat toDateFormat() {
		StringBuilder formatBuilder = new StringBuilder();
		if (yearSuffix != null) {
			formatBuilder.append("yyyy" + yearSuffix);
		}

		if (monthSuffix != null) {
			formatBuilder.append("MM" + monthSuffix);
		}

		if (daySuffix != null) {
			formatBuilder.append("dd" + daySuffix);

			// add blank space if have hourSuffix unit
			if (hourSuffix != null) {
				formatBuilder.append(" ");
			}
		}

		if (hourSuffix != null) {
			formatBuilder.append("HH" + hourSuffix);
		}

		if (minuteSuffix != null) {
			formatBuilder.append("mm" + minuteSuffix);
		}

		if (secondSuffix != null) {
			formatBuilder.append("ss" + secondSuffix);
		}
		return new SimpleDateFormat(formatBuilder.toString(), Locale.getDefault());
	}

	public String getYearSuffix() {
		return yearSuffix;
	}

	public void setYearSuffix(String yearSuffix) {
		this.yearSuffix = yearSuffix;
	}

	public String getMonthSuffix() {
		return monthSuffix;
	}

	public void setMonthSuffix(String monthSuffix) {
		this.monthSuffix = monthSuffix;
	}

	public String getDaySuffix() {
		return daySuffix;
	}

	public void setDaySuffix(String daySuffix) {
		this.daySuffix = daySuffix;
	}

	public String getHourSuffix() {
		return hourSuffix;
	}

	public void setHourSuffix(String hourSuffix) {
		this.hourSuffix = hourSuffix;
	}

	public String getMinuteSuffix() {
		return minuteSuffix;
	}

	public void setMinuteSuffix(String minuteSuffix) {
		this.minuteSuffix = minuteSuffix;
	}

	public String getSecondSuffix() {
		return secondSuffix;
	}

	public void setSecondSuffix(String secondSuffix) {
		this.secondSuffix = secondSuffix;
	}

}